# Создаем CSV файл
with open('culture_quiz.csv', mode='w', newline='', encoding='utf-8') as file:
    writer = csv.writer(file)
    writer.writerow(['ID', 'Question', 'Answer1', 'Answer2', 'Answer3', 'Answer4', 'CorrectAnswer'])

    # Заполняем файл вопросами
    for i, q in enumerate(questions, 1):
        writer.writerow([i, q['question'], q['answers'][0], q['answers'][1], q['answers'][2], q['answers'][3], q['correct']])

print("Файл 'culture_quiz.csv' успешно создан!")